import axios from 'axios';
import React, { useState } from 'react'
import { useHistory } from 'react-router';
import styled from 'styled-components';

const BaseUrl = "https://delta-inspiration.herokuapp.com/api/seller-forgot/";
export default function Forget() {
    const [email,setemail] = useState();
    const history = useHistory()
    console.log(email)
    const handleforgotpass = () => {
        console.log(email)
        axios.post(BaseUrl,{
            "email" : email
        }).then((res)=>{
            alert(res.data.result)
            history.push('/fverify')
            localStorage.setItem('email',email)
        }).catch((err)=>{alert(err)})
    }
    return (
        <Container>
            <input onChange={(e)=>setemail(e.target.value)} type="email"placeholder="Email"></input>
            <button onClick={handleforgotpass}>Done</button>
        </Container>
    )
}

const Container = styled.div`
margin-top: 30px;
display: flex;
align-items: center;
flex-direction: column;
margin-left: auto;
margin-right: auto;
padding:30px;
height: 80vh;
width: 30vw;
background: rgba(255, 255, 255, 0.15);
box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);
backdrop-filter: blur(8.5px);
-webkit-backdrop-filter: blur(8.5px);
border-radius: 10px;
color: #ffffff;
text-transform: uppercase;
letter-spacing: 0.4rem;

h1 {
    color: black;
}

@media only screen and (max-width: 320px) {
  width: 80vw;
  height: 90vh;
  hr {
    margin-bottom: 0.3rem;
  }
  h4 {
    font-size: small;
  }
}
@media only screen and (min-width: 360px) {
  width: 80vw;
  height: 90vh;
  h4 {
    font-size: small;
  }
}
@media only screen and (min-width: 411px) {
  width: 80vw;
  height: 90vh;
}

@media only screen and (min-width: 768px) {
  width: 80vw;
  height: 80vh;
}
@media only screen and (min-width: 1024px) {
  width: 70vw;
  height: 50vh;
}
@media only screen and (min-width: 1280px) {
  width: 30vw;
  height: 70vh;
}

`;
